$("#post-content a").each(function() {
    if (this.text.indexOf('[') != -1) {
        //alert(this.innerHTML);
        //var toTopLink = document.createElement("span");
		//toTopLink.href =  this.href;
		//toTopLink.setAttribute("class", "bttn");
        var TopLink = this.innerHTML.trim();
        var replaced = TopLink.replace(/]/g, '');
        replaced = replaced.replace(/\/[\/g, '');

        var t = document.createTextNode(replaced);
        this.innerHTML = "";
        this.appendChild(t)
        //this.innerHTML = id.replace(/[+/g, "").replace(/]+/g, ""); 
		this.setAttribute("class", "bttn");
    }
});


